BEGIN TRANSACTION;
CREATE TABLE 'codes' ('code_hash' TEXT NOT NULL);
CREATE TABLE 'announcements' ('id' INTEGER PRIMARY KEY NOT NULL, 'text' TEXT NOT NULL, 'by' INTEGER NOT NULL DEFAULT 0 ,'date' INTEGER NOT NULL);
CREATE TABLE 'collections' ('id' INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, 'type' INTEGER NOT NULL, 'by' INTEGER NOT NULL, 'created_at' INTEGER NOT NULL, 'last_updated' INTEGER DEFAULT NULL, 'data' TEXT NOT NULL , 'is_pfp' BOOLEAN NOT NULL DEFAULT 0, 'name'  TEXT NOT NULL  );
CREATE TABLE 'collection_ratings' ('rater' INTEGER NOT NULL, 'collection' INTEGER NOT NULL, 'is_positive' BOOLEAN NOT NULL DEFAULT 1 );
CREATE TABLE 'marketplace_listings' ('id' INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, 'user' INTEGER NOT NULL, 'gem' INTEGER NOT NULL, 'amount' INTEGER NOT NULL, 'price' INTEGER NOT NULL, 'is_buying' BOOLEAN NOT NULL);
CREATE TABLE 'users' ('id' INTEGER PRIMARY KEY NOT NULL ,'identificator_id' TEXT NOT NULL , 'is_admin' BOOLEAN NOT NULL DEFAULT 0, 'is_premium' BOOLEAN NOT NULL DEFAULT 0 , 'money' INTEGER NOT NULL DEFAULT 0, '0' INTEGER NOT NULL DEFAULT 0, '1' INTEGER NOT NULL DEFAULT 0, '2' INTEGER NOT NULL DEFAULT 0, '3' INTEGER NOT NULL DEFAULT 0, '4' INTEGER NOT NULL DEFAULT 0, '5' INTEGER NOT NULL DEFAULT 0, '6' INTEGER NOT NULL DEFAULT 0, '7' INTEGER NOT NULL DEFAULT 0, '8' INTEGER NOT NULL DEFAULT 0, '9' INTEGER NOT NULL DEFAULT 0, '10' INTEGER NOT NULL DEFAULT 0, '11' INTEGER NOT NULL DEFAULT 0, '12' INTEGER NOT NULL DEFAULT 0, 'shifts_completed' INTEGER NOT NULL DEFAULT 0,'energy' INTEGER NOT NULL DEFAULT 50, 'name'  TEXT NOT NULL  , 'date_signed_up'  INTEGER NOT NULL  , 'last_login'  INTEGER NOT NULL  , 'read_announcements' BOOLEAN NOT NULL DEFAULT 1, 'date_became_premium' INTEGER DEFAULT NULL, 'location' INTEGER NOT NULL DEFAULT 0);
